import React from 'react';
import logo from './logo.svg';
import './App.css';

class App extends React.Component {

  constructor(props) {
      super(props);
      const { activeItems } = this.props;
      this.state = {
          activeElements: activeItems.length ?
              activeItems.map((item, index) => this.constructor.getItemId(index)) :
              [],
      };
      this.handleClick = this.handleClick.bind(this);
  }

  handleClick(id) {
      let actives = this.state.activeElements;
      if (this.props.exclusive) {
          actives = [];
          if (!this.state.activeElements.includes(id)) {
              actives.push(id);
          }
          this.setState({ activeElements: actives });
      } else {
          if (actives.includes(id)) {
              actives = actives.filter(item => item !== id);
          } else {
              actives.push(id);
          }
          this.setState({ activeElements: actives });
      }
  }

  render() {
      const { children, triggerSize, ...otherProps } = this.props;

      return (
          <AccordionWrapper {...otherProps}>
              {React.Children.map(children, (item, i) => {
                  const elemId = this.constructor.getItemId(i);
                  return React.cloneElement(item, {
                      active: this.state.activeElements.includes(elemId),
                      onClick: this.handleClick,
                      triggerSize,
                      elemId,
                  });
              })}
          </AccordionWrapper>
      );
  }
}

Accordion.propTypes = propTypes;
Accordion.defaultProps = defaultProps;

export default App;
